let pattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$/;
let req = new RegExp(pattern);
let result = req.test("SomePass1");
console.log('pass1 w/ object', result);

let patternStr = '^(?=.*\\d)(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$';
let req2 = new RegExp(patternStr);
let result2 = req2.test("SomePass1");
console.log('pass2 w/ string', result2);

let patternStr2 = '^(?=.*[a-z])(?=.*[A-Z])[0-9a-zA-Z]{8,}$';
let req3 = new RegExp(patternStr2);
let result3 = req3.test("SomePass1");
console.log('pass3 w/ modified string', result3);

