const path = require('path');
const fs = require('fs');

const directoryPath = path.join(__dirname, '../src/lang');

fs.readdir(directoryPath, (err, files) => {
  if (err) {
    return console.log('Unable to scan directory: ' + err);
  }
  let count = 0;
  files.forEach((file) => {
    console.log(file);
    let lang = require(path.join(directoryPath, file));
    if(lang.hasOwnProperty('attributes')) {
      count++;
    }
  });
  console.log(files.length, count);
});